'use strict';
const Event = require('events');
const Code = require('code');
const Lab = require('lab');
const lab = exports.lab = Lab.script();

const describe = lab.describe;
const it = lab.it;
const expect = Code.expect;

const Pre = require('../../../../lib/rules/rules-callback').ContinueTracingCB().pre;
const Post = require('../../../../lib/rules/rules-callback').ContinueTracingCB().post;

const getArguments = function () {

    return arguments;
};

describe('Callback', () => {

    describe('ContinueTracingCB', () => {

        it('should do nothing without session', { plan: 6 }, (done) => {

            expect(Pre(null, null, null, null, null)).to.not.exist();
            expect(Pre(null, null, null, null, {})).to.not.exist();
            expect(Post(null, null, null, null, {})).to.not.exist();
            expect(Post(null, {}, null, null, {})).to.not.exist();
            expect(Post(null, new Event(), null, null, { raw: { bindEmitter: function (emitter) {

                expect(emitter instanceof Event).to.be.true();
            } } })).to.not.exist();
            done();
        });

        it('should wrap diverse callbacks without rule', { plan: 2 }, (done) => {

            const args = getArguments(() => 0, () => 1, 3);

            Pre(args, null, null, null, {
                raw: {
                    bind: function (f) {

                        expect(f).to.exist();
                    }
                }
            });

            done();
        });

        it('should wrap diverse callbacks rule', { plan: 1 }, (done) => {

            const args = getArguments(() => 0, () => 1);

            Pre(args, null, {
                data:
                {
                    values:
                        [{ bindRanks: [0] }]
                }
            }, null, {
                raw: {
                    bind: function (f) {

                        expect(f).to.exist();
                    }
                }
            });

            done();
        });

        it('should wrap diverse callbacks rule', { plan: 2 }, (done) => {

            const args = getArguments(() => 0, () => 1);

            Pre(args, null, {
                data:
                {
                    values:
                        [{ }]
                }
            }, null, {
                raw: {
                    bind: function (f) {

                        expect(f).to.exist();
                    }
                }
            });

            done();
        });

        it('should wrap diverse callbacks rule', { plan: 2 }, (done) => {

            const args = getArguments(() => 0, () => 1);

            Pre(args, null, {
                data:
                {
                    values:
                        []
                }
            }, null, {
                raw:
                {
                    bind: function (f) {

                        expect(f).to.exist();
                    }
                }
            });

            done();
        });

        it('should wrap diverse callbacks rule', { plan: 2 }, (done) => {

            const args = getArguments(() => 0, () => 1);

            Pre(args, null, {
                data: {}
            }, null, {
                raw: {
                    bind: function (f) {

                        expect(f).to.exist();
                    }
                }
            });

            done();
        });

        it('should wrap diverse callbacks rule', { plan: 2 }, (done) => {

            const args = getArguments(() => 0, () => 1);

            Pre(args, null, {}, null, {
                raw: {
                    bind: function (f) {

                        expect(f).to.exist();
                    }
                }
            });

            done();
        });
    });
});
